import React from 'react';
import PropTypes from "prop-types";
import SpeechRecognition from "react-speech-recognition";
import Button from '@enact/moonstone/Button';
import Marquee from '@enact/moonstone/Marquee';
const propTypes = {
  transcript: PropTypes.string,
    resetTranscript: PropTypes.func,
    browserSupportsSpeechRecognition: PropTypes.bool,
  };

  const SpeechText = ({
    transcript,
    resetTranscript,
    browserSupportsSpeechRecognition,
  }) => {
    if (!browserSupportsSpeechRecognition) {
      return null;
    }
    return (
      <div>
         <Marquee marqueeOn="render">Welcome to LG Hackathon. This is a notepad app for tv where users can turn there speech to text</Marquee>
        <br/><Button onClick={resetTranscript}>Reset</Button>
        <div className = "GetText">{transcript}</div>
      </div>
    );
  };
  SpeechText.propTypes = propTypes;
export default SpeechRecognition(SpeechText);
