// Type definitions for ui/A11yDecorator

type Omit<T, K extends keyof T> = Pick<T, Exclude<keyof T, K>>;
type Merge<M, N> = Omit<M, Extract<keyof M, keyof N>> & N;

export interface A11yDecoratorConfig extends Object {
  /**
   * Configures the prop for the source of the component's content
   */
  prop?: string;
}
export interface A11yDecoratorProps {
  /**
   * Sets the hint text to be read after the content.
   */
  accessibilityHint?: string;
  /**
   * Sets the hint text to be read before the content.
   */
  accessibilityPreHint?: string;
  /**
   * Sets the value of the  `aria-label`  attribute for the wrapped component.
   */
  "aria-label"?: string;
}
export function A11yDecorator<P>(
  config: A11yDecoratorConfig,
  Component: React.ComponentType<P> | string
): React.ComponentType<P & A11yDecoratorProps>;

export function A11yDecorator<P>(
  Component: React.ComponentType<P> | string
): React.ComponentType<P & A11yDecoratorProps>;

export default A11yDecorator;
