// Type definitions for ui/Scroller

import * as React from "react";
import { ScrollableProps as ui_Scrollable_ScrollableProps } from "@enact/ui/Scrollable";
import { ScrollerBaseProps as ui_Scrollable_ScrollerBaseProps } from "@enact/ui/Scrollable";

type Omit<T, K extends keyof T> = Pick<T, Exclude<keyof T, K>>;
type Merge<M, N> = Omit<M, Extract<keyof M, keyof N>> & N;

export interface ScrollerBaseProps {
  /**
   * Direction of the scroller.
   *
   * Valid values are:
   * *  `'both'` ,
   * *  `'horizontal'` , and
   * *  `'vertical'` .
   */
  direction?: string;
}
/**
 * An unstyled base scroller component.
 *
 * In most circumstances, you will want to use the Scrollable version.
 */

export class ScrollerBase extends React.Component<
  ScrollerBaseProps & React.HTMLProps<HTMLElement>
> {}

export interface ScrollerProps
  extends Merge<
    ui_Scrollable_ScrollableProps,
    ui_Scrollable_ScrollerBaseProps
  > {
  /**
   * Direction of the scroller.
   *
   * Valid values are:
   * *  `'both'` ,
   * *  `'horizontal'` , and
   * *  `'vertical'` .
   */
  direction?: string;
}
/**
 * An unstyled scroller.
 * 
 * Example:
 * ```
<Scroller>Scroll me.</Scroller>
```
 */

export class Scroller extends React.Component<
  ScrollerProps & React.HTMLProps<HTMLElement>
> {}

export default Scroller;
